import { useState, useEffect } from "react";

const useFetch = (url) => {
  const [data, setData] = useState(null);

  useEffect(() => {
    // fetch(url)
    //   .then((res) => res.json())
    //   .then((data) => setData(data));

    const fetchData = async () => {
      const res = await fetch(url);
      const data = await res.json();
      setData(data);
    };
  }, [url]);

  return {
    data,
  };
};

export default useFetch;
